      logger.log(Level.INFO, "fileName is : {0}", fileName);
      logger.log(Level.INFO, "headers are : {0}", headers);
      request.setAttribute("fileName", fileName);
      request.setAttribute("filePath", filePath);
      request.setAttribute("headers", headers);
    } catch (Exception e) {
